import React, { useState, useEffect } from "react";
import Skeleton from "react-loading-skeleton";
import useUser from "../../hooks/use-user";
import { isUserFollowingProfile, toggleFollow } from "../../services/firebase";

function Image({ src, caption }) {
  return (
    <div className="imgdiv">
      <img src={src} alt={caption} className="img" />
    </div>
  );
}

function Comment({ showing, content, comments }) {
  const [showing1, setshowing1] = useState(showing);
  return (
    <>
      {!showing1 ? (
        <>
          {comments.slice(0, 3).map((item) => (
            <p key={`${item.comment}-${item.displayName}`} className="mb-1">
              <Link to={`/p/${item.displayName}`}>
                <span className="mr-1 font-bold">{item.displayName}</span>
              </Link>
              <span>{item.comment}</span>
            </p>
          ))}
        </>
      ) : (
        <>
          <div className="modal fixed w-full h-full top-0 left-0 flex items-center justify-center z-50">
            <div className="modal-overlay fixed w-full h-full overflow-y-scroll bg-gray-900 left-0 right-0 opacity-50 -z-10"></div>
            <div className="modal-container bg-white w-full md:w-1/2 mx-auto rounded shadow-lg overflow-y-auto">
              <div className="image">
                <Image src={content.imageSrc} caption={content.caption} />
              </div>
              <div
                className="x"
                onClick={() => {
                  setShowing(!showing);
                }}
              >
                ‚ùå
              </div>
              <div className="head">Comments :</div>
              <div className="sidecomment">
                {comments.map((item) => (
                  <div
                    key={`${item.comment}-${item.displayName}`}
                    className="mb-1 classes"
                  >
                    <Link to={`/p/${item.displayName}`}>
                      <img
                        className="rounded-full w-8 flex mr-3 imgclass"
                        src={`/images/avatars/${item.displayName}.jpg`}
                        alt={`Follow ${item.displayName}`}
                      />
                    </Link>
                    <Link to={`/p/${item.displayName}`}>
                      <span className="name">{item.displayName}:</span>
                    </Link>
                    <span className="comm">{item.comment}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </>
      )}
    </>
  );
}

export default function Header({
  photosCount,
  followerCount: followers,
  setFollowerCount,
  username: profileUsername,
  profile: {
    docId: profileDocId,
    userId: profileUserId,
    fullName,
    following = [],
  },
}) {
  const { user } = useUser();
  const [isFollowingProfile, setIsFollowingProfile] = useState(false);
  const activeBtnFollow =
    user && user.username && user.username !== profileUsername;

  const handleToggleFollow = async () => {
    setIsFollowingProfile((isFollowingProfile) => !isFollowingProfile);
    setFollowerCount({
      followerCount: isFollowingProfile ? followers - 1 : followers + 1,
    });
    await toggleFollow(
      isFollowingProfile,
      user.docId,
      profileDocId,
      profileUserId,
      user.userId
    );
  };

  useEffect(() => {
    const isLoggedInUserFollowingProfile = async () => {
      const isFollowing = await isUserFollowingProfile(
        user.username,
        profileUserId
      );
      setIsFollowingProfile(isFollowing);
    };

    if (user.username && profileUserId) {
      isLoggedInUserFollowingProfile();
    }
  }, [user.username, profileUserId]);

  return (
    <div className="grid grid-cols-3 gap-4 justify-between mx-auto max-w-screen-lg">
      <div className="container flex justify-center">
        <img
          className="rounded-full h-40 w-40 flex"
          alt={`${profileUsername} profile picture`}
          src={`/images/avatars/${profileUsername}.jpg`}
        />
      </div>
      <div className="flex items-center justify-center flex-col col-span-2">
        <div className="container flex items-center">
          <p className="text-2xl mr-4">{profileUsername}</p>
          {activeBtnFollow && (
            <button
              className="bg-blue-500 font-bold text-sm rounded text-white w-20 h-8"
              type="button"
              onClick={handleToggleFollow}
            >
              {isFollowingProfile ? "Unfollow" : "Follow"}
            </button>
          )}
        </div>
        <div className="container flex mt-4">
          {followers === undefined || following === undefined ? (
            <Skeleton count={1} width={677} height={24} />
          ) : (
            <>
              <p className="mr-10">
                <span className="font-bold">{photosCount}</span> photos
              </p>
              <p className="mr-10">
                <span className="font-bold">{followers}</span>{" "}
                {followers === 1 ? "follower" : "followers"}
              </p>
              <p className="mr-10">
                <span className="font-bold">{following.length}</span> following
              </p>
            </>
          )}
        </div>
        <div className="container mt-4">
          <p className="font-medium">
            {!fullName ? <Skeleton count={1} height={24} /> : fullName}
          </p>
        </div>
      </div>
    </div>
  );
}
export { Comment };
