import React, { lazy, Suspense } from "react";
import useAuthListener from "./hooks/use-auth-listener";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import UserContext from "./context/user";
import IsUserLoggedIn from "./helper.js/is-user-logged-in";
//browseRouter used to change page with url
//switch route the first value matches
//route path of route
import * as ROUTES from "./constants/routes";
const Dashboard = lazy(() => import("./pages/dashboard"));
//lazy creates a seperate chunk file and make render when it needed
const Login = lazy(() => import("./pages/login"));
const SignUp = lazy(() => import("./pages/signup"));
const Profile = lazy(() => import("./pages/profile"));
const NotFound = lazy(() => import("./pages/not-found"));
// {/* suspense used to wait untill chunk file load untill it shows fall back */}

export default function App() {
  const { user } = useAuthListener();

  return (
    <UserContext.Provider value={{ user }}>
      <Router>
        <Suspense fallback={<p>Loading...</p>}>
          <Switch>
            <IsUserLoggedIn
              user={user}
              loggedInPath={ROUTES.DASHBOARD}
              path={ROUTES.LOGIN}
            >
              <Login />
            </IsUserLoggedIn>
            <Route path={ROUTES.LOGIN} component={Login} />
            <Route path={ROUTES.SIGN_UP} component={SignUp} />
            <Route path={ROUTES.PROFILE} component={Profile} />
            <Route path={ROUTES.DASHBOARD} component={Dashboard} exact />
            <Route component={NotFound} />
          </Switch>
        </Suspense>
      </Router>
    </UserContext.Provider>
  );
}
